function swaptoSuper() {
	var g:MovieClip = MovieClip(parent);
	if (g.objectsArr) {
		for (var i:int = 0; i < g.objectsArr.length; i ++) {
			if (this == g.objectsArr[i]) {
				g.objectsArr.splice(i,1);
				trace("** found and removed hero");
				break;0
			}
		}

		trace("** SWITCH **");
		g.hero = g.hero3;
		g.hero.x = this.x;
		g.hero.y = this.y;
		g.hero.init(1, true);
		g.hero.visible = true;
		this.visible = false;

		g.objectsArr.unshift(g.hero);

		g.hero.xSpeed = xSpeed;
		g.hero.ySpeed = ySpeed;
		g.hero.scaleX = this.scaleX;
		g.hero.active = this.active;
		g.hero.moveable = this.moveable;
		g.hero.gotoAndStop(currentFrameLabel);
		g.hero[currentFrameLabel].gotoAndPlay(this[currentFrameLabel].currentFrame-1);

		for (i = 0; i < g.objectsArr.length; i ++) {
			var obj:MovieClip = g.objectsArr[i]
			if (obj.hasOwnProperty("hero")) {
				obj.hero = g.hero;
			}
			if (obj.hasOwnProperty("party1") || obj.hasOwnProperty("party2") ) {
				if (obj.party1 == this) obj.party1 = g.hero;
				if (obj.party2 == this) obj.party2 = g.hero;
				obj.defineText();
			}

		}
		for (i = 0; i < g.enemiesArr.length; i ++) {
			obj = g.enemiesArr[i]
			if (obj.hasOwnProperty("hero")) {
				obj.hero = g.hero;
			}
		}
		if (vcam.target == this) vcam.target = g.hero;


	}
}
