visible = false;

import flash.display.MovieClip;
import flash.events.Event;
import com.greensock.TweenMax;

//numbers
var currentWave:int = MovieClip(root).sessionStartingWave;//default is 0
//var currentWave:int = 8;//default is 0
var spawnTimer:Number;
var spawnTimerDefult:Number;
var maxEnemies:Number;
var numOfEnemiesInWave:int;
var enemiesSpawned:Number;
var endWave:Number;

//bools
var firtWaveDisplay:Boolean;
var flagWaveComplete:Boolean;
var bossRound:Boolean;


//list of enemies to spawn in wave - string names
var enemyWaveList:Array;
var bossWaveList:Array;

var enemiesArr:Array; //enemy instance ref

//pool arrays
var enemy1Pool:Array;
var enemy2Pool:Array;
var enemy3Pool:Array;
var enemy4Pool:Array;
var enemy5Pool:Array;
var enemy6Pool:Array;
var enemy7Pool:Array;
var boss1Pool:Array;
var boss2Pool:Array;
var boss3Pool:Array;
var boss4Pool:Array;

//reference
var game:MovieClip;
var gameData:MovieClip;
var hud:MovieClip;
var _root:MovieClip;
var particleLayer:MovieClip;
var lastSpawnedEnemy:MovieClip;

//compontend
var mode:String;
if (!mode) mode = "Standard";
trace("MODE :: "+mode);
var survivalType:Survival;
var enemyBaseLevel:Number;

//init();

function init():void {
	endWave = 3;//was 3

	enemyWaveList = new Array();
	bossWaveList = new Array();
	enemiesArr = new Array();

	game = MovieClip(parent);
	hud = MovieClip(root).hud;
	_root = MovieClip(root);
	particleLayer = game.particleLayer;
	gameData = MovieClip(root);

	switch (mode) {
		case "Standard":
			survivalType = new SurvivalStandard();
			break;
		case "Cliff":
			survivalType = new SurvivalCliff();
			break;
		case "Boss":
			survivalType = new SurvivalBoss();
			break;
		case "Story":
			survivalType = new SurvivalStory();
			break;
		case "Story2":
			survivalType = new SurvivalStory2();
			break;
		case "Test":
			survivalType = new SurvivalTest();
			break;
		case "Soldiers":
			survivalType = new SurvivalSoldiers();
			break;
		case "Web":
			survivalType = new SurvivalWeb();
			break;
	}

	hud.waveCompleteDisplay.addEventListener("COMPLETE", nextWave);

	pool();
	nextWave(null);
}

function update():void {
	if (!game.hasLevelBegun) {

	} else {
		if (game.hero.life > 0) {
			//timer and spawn
			spawnTimeUpdate();
			//return the dead to pools
			returnDeadtoPool();
			//handleWavecomplete
			waveComplete();

		}
	}	
}

function nextWave(e:Event):void {

	currentWave ++;
	if (_root.gameMode != _root.SURVIVAL && currentWave > endWave) {
		this.dispatchEvent(new Event("STORYEND"));
	} else {
		//reset
		if (_root) _root.sessionWave = currentWave;	
		enemiesSpawned = 0;
		flagWaveComplete = false;

		//update
		_progression();

		//display wave
		//if (_root.gameMode == _root.SURVIVAL) {
			TweenMax.delayedCall(80, function () {hud.displayWave(currentWave);}, null, true);
		//}

		//boss round
		if (bossRound && survivalType.doBoss) {
			TweenMax.delayedCall(40, spawnNextBoss, null, true);
		}

		this.dispatchEvent(new Event("STARTNEWWAVE"));
	}
}

function spawnTimeUpdate():void {
	if (numOfEnemiesInWave > 0) {
		if (spawnTimer > 0) {
			spawnTimer --;
		} else {
			trace("curr en: "+getNumOfEnemies()+", max: "+maxEnemies);
			if (getNumOfEnemies() < maxEnemies) {

				enemiesSpawned = game.enemiesArr.length;
				for (var i:int = 0; i < game.enemiesArr.length; i ++) {
					var enemyType:String = game.enemiesArr[i].enemyType;
					if (enemyType) {
						if (enemyType.substring(0, 5) == "ENEMY" || enemyType.substring(0, 4) == "BOSS" ) {
						} else {
							enemiesSpawned --;
						}
					} else {
						enemiesSpawned --;
					}
				}

				spawnNextEnemy();

			}
			spawnTimer = spawnTimerDefult;
		}
	}
}

function returnDeadtoPool():void {
	if (enemiesArr.length > 0) {
		for (var i:int = 0; i < enemiesArr.length; i ++) {
			var obj:MovieClip = enemiesArr[i];
			var arr:Array;
			switch (obj.enemyType) {
				//determin correct arr
				case "ENEMY1": arr = enemy1Pool; break;
				case "ENEMY2": arr = enemy2Pool; break;
				case "ENEMY3": arr = enemy3Pool; break;
				case "ENEMY4": arr = enemy4Pool; break;
				case "ENEMY5": arr = enemy5Pool; break;
				case "ENEMY6": arr = enemy6Pool; break;
				case "ENEMY7": arr = enemy7Pool; break;
				case "BOSS1": arr = boss1Pool; break;
				case "BOSS2": arr = boss2Pool; break;
				case "BOSS3": arr = boss3Pool; break;
				case "BOSS4": arr = boss4Pool; break;
			}

			if (arr) {
				for (var e:int = 0; e < arr.length; e ++) {
					if (arr[e][0] == obj) {
						if (obj.dead && obj.currentFrameLabel == "death") {
							if (obj.death.currentFrame == obj.death.totalFrames) {
								returnEnemy(obj);
							}
						}
					}
				}
			} else {
				//game.enemiesArr.splice(i, 1);
				//game.removeChild(obj);
				trace("Enemy type cannot be returned - Perhaps this enemy is an IceShot or Bomb, or boulder");
			}
		}
	}
}

function waveComplete():void {
	if (!flagWaveComplete) {
		if (numOfEnemiesInWave <= 0) {
			if (enemiesArr.length == 0) {
				hud.displayWaveComplete();
				flagWaveComplete = true;
				this.dispatchEvent(new Event("WAVECOMPLETE"));
				trace("Enemies Killed :: "+gameData.sessionSurvivalEnemies);
				trace("Bosses Killed :: "+gameData.sessionSurvivalBosses);
			}
		}
	}
}

function spawnNextEnemy():void {
	for (var i:int = 0 ; i < enemyWaveList.length; i ++) {
		if (enemyWaveList[i][1]) {
			spawnEnemy(enemyWaveList[i][0]);
			enemyWaveList[i][1] = false;
			break;
		}
	}
}
function spawnNextBoss():void {
	for (var i:int = 0; i < bossWaveList.length; i ++) {
		spawnBoss(bossWaveList[0][0], i+1);
	}
}

function spawnEnemy(enemyID:String):void {
	var arr:Array;
	switch (enemyID) {
		case "ENEMY1":
			arr = enemy1Pool;
			break;
		case "ENEMY2":
			arr = enemy2Pool;
			break;
		case "ENEMY3":
			arr = enemy3Pool;
			break;
		case "ENEMY4":
			arr = enemy4Pool;
			break;
		case "ENEMY5":
			arr = enemy5Pool;
			break;
		case "ENEMY6":
			arr = enemy6Pool;
			break;
		case "ENEMY7":
			arr = enemy7Pool;
			break;

	}

	var enemy:MovieClip = getEnemy(arr);
	if (enemy) {

		var spawner:MovieClip;
		var numOfSpawners:Number = getNumberOfSpawners()-1;
		var spawnerNum:Number = Math.ceil(Math.random()*numOfSpawners);
		spawner = game["spawnPoint"+spawnerNum];

		var r:Number = (Math.random()*100) - (Math.random()*100);
		enemy.x = spawner.x + r;
		enemy.y = spawner.y;
		enemy.aware = true;
		enemy.angry = true;

		//ditermin enemy difficulty
		if (!enemyBaseLevel) enemyBaseLevel = 0;
		var difMult:Number = 1.25;
		var _enemyLev:Number;

		//if (gameData.gameDifficulty == gameData.gameDifficultyfutile) difMult = 1.5;
		//_enemyLev = Math.round(enemyBaseLevel*difMult) + Math.round(currentWave*.5) + 1;	
		_enemyLev = 1 + Math.round(currentWave*difMult);	

		enemy.init(_enemyLev);
		enemy.aware = true;

		//super enemy
		switch (enemyID) {
			case "ENEMY1":
				if (currentWave >= 8) enemy.superEnemy = true;
				break;
			case "ENEMY2":
				if (currentWave >= 10) enemy.superEnemy = true;
				break;
			case "ENEMY6":
				if (currentWave >= 6) enemy.superEnemy = true;
				break;
			case "ENEMY7":
				if (currentWave >= 7) enemy.superEnemy = true;
				break;
		}

		enemy.gotoAndStop("SPAWN");
		if (enemyID == "ENEMY4") {
			enemy.SPAWN.gotoAndPlay(2);
		}

		enemiesArr.push(enemy);
		game.enemiesArr.push(enemy);
		numOfEnemiesInWave --;

		lastSpawnedEnemy = enemy;
		this.dispatchEvent(new Event("ENEMYSPAWNED"));

		if (enemyID != "ENEMY4")
			particleLayer.addLightning(enemy.x, enemy.y);
	} else {
		trace("something went wrong in a good way. too few enemies in survival pool? Huge null crash avoided.");
		numOfEnemiesInWave --;
	}
}

function spawnBoss(bossID:String, pos:int = 1):void {
	var arr:Array;
	switch (bossID) {
		case "BOSS1":
			arr = boss1Pool;
			break;
		case "BOSS2":
			arr = boss2Pool;
			break;
		case "BOSS3":
			arr = boss3Pool;
			break;
		case "BOSS4":
			arr = boss4Pool;
			break;
	}



	var enemy:MovieClip = getEnemy(arr);

	var spawner:MovieClip;
	if (pos == 1) {
		spawner = game.spawnPointBoss1;
	} else if (pos == 2) {
		spawner = game.spawnPointBoss2;
	}

	enemy.x = spawner.x;
	enemy.y = spawner.y;

	//ditermin enemy difficulty
	if (!enemyBaseLevel) enemyBaseLevel = 0;
	var difMult:Number = 1;
	var _enemyLev:Number;
	if (gameData.gameDifficulty == gameData.gameDifficultyfutile) difMult = 1.5;
	_enemyLev = Math.round(enemyBaseLevel*difMult) + Math.round(currentWave*.5) + 1;	

	enemy.init(_enemyLev);
	if (enemy.initSurvival) enemy.initSurvival();
	enemy.aware = true;
	//enemy.gotoAndStop("SPAWN");
	enemiesArr.push(enemy);
	game.enemiesArr.push(enemy);
	_root.hud.bossBar.add(enemy);
	trace(game.enemiesArr);
}

//POOLING FUNCTIONS//---------------------------------------------//
////////////////////////////////////////////////////////////////////
function getEnemy(poolArray:Array):MovieClip {
	for (var i:int = 0; i < poolArray.length; i ++) {
		if (!poolArray[i][1]) {
			var obj:MovieClip = MovieClip(poolArray[i][0]);
			removeChild(obj);
			game.addChild(obj);
			obj.visible = true;
			poolArray[i][1] = true;
			return obj;
		}
	}
	return null;
}

function returnEnemy(obj:MovieClip):void {
	var poolArray:Array;

	switch (obj.enemyType) {
		case "ENEMY1": poolArray = enemy1Pool; break;
		case "ENEMY2": poolArray = enemy2Pool; break;
		case "ENEMY3": poolArray = enemy3Pool; break;
		case "ENEMY4": poolArray = enemy4Pool; break;
		case "ENEMY5": poolArray = enemy5Pool; break;
		case "ENEMY6": poolArray = enemy6Pool; break;
		case "ENEMY7": poolArray = enemy7Pool; break;
		case "BOSS1": poolArray = boss1Pool; break;
		case "BOSS2": poolArray = boss2Pool; break;
		case "BOSS3": poolArray = boss3Pool; break;
		case "BOSS4": poolArray = boss4Pool; break;
	}

	for (var i:int = 0; i < poolArray.length; i ++) {
		if (poolArray[i][1] && poolArray[i][0] == obj) {
			for (var e:int = 0; e < game.enemiesArr.length; e++) {
				if (game.enemiesArr[e] == obj) {
					game.enemiesArr.splice(e,1);
				}
			}
			for (e = 0; e < enemiesArr.length; e ++) {
				if (enemiesArr[e] == obj) {
					enemiesArr.splice(e,1);
				}
			}

			game.removeChild(obj);
			this.addChild(obj);
			obj.visible = false;

			poolArray[i][1] = false;
			break;
		}
	}
}

