function getNumberOfSpawners():Number {
	for (var i:Number = 1; i < 20; i ++) {
		if (game["spawnPoint"+i]) {

		} else {
			break;

		}
	}
	return i;
}

function getNumOfEnemies():int {
	var count:int = 0;

	for (var i:int = 0; i < MovieClip(parent).enemiesArr.length; i ++) {
		var en:MovieClip = MovieClip(parent).enemiesArr[i];
		if (en) {
			if (en.hasOwnProperty("enemyType")) {
				if (String(en.enemyType).slice(0,5) == "ENEMY") {
					count++;
				}				
			}
		}
	}

	return count;
}
