function kill ():void {
	if (hud && hud.waveCompleteDisplay) 
	hud.waveCompleteDisplay.removeEventListener("COMPLETE", nextWave);
	//game.removeChild(this);
	survivalType = null;
}

function _progression ():void {
	trace("Increasing Difficulty");

	//determine time between enemy spawn
	trace("spawn time ::    " +survivalType.spawnTimerDefult);
	spawnTimerDefult = survivalType.spawnTimerDefult-(currentWave*15);//was 10
	if (spawnTimerDefult < 40) spawnTimerDefult = 40;//was 40
	spawnTimer = spawnTimerDefult;



	_pickEnemies();
	_pickBoss();


}

function _pickEnemies(boss:Boolean = false) {
	//determine amount of enemies per wave
	numOfEnemiesInWave = 4+(Math.round(currentWave*1.5));
	//determine max enemies on screen - no greater than 15
	maxEnemies = 2 + Math.round(currentWave*.6);//was 5
	if (maxEnemies > 15) maxEnemies = 15;

	if (bossRound) {

		numOfEnemiesInWave = Math.ceil(numOfEnemiesInWave * .3);
		maxEnemies = Math.ceil(maxEnemies * .3);
	}


	enemyWaveList = [];
	/*
	for (var i:int = 0; i < numOfEnemiesInWave; i ++) {

		var enemyID:String;

		//defult
		if (currentWave%3 == 0) enemyID = "ENEMY4";
		else enemyID = "ENEMY1";

		//progressive
		if (currentWave >= 3) {
			if (i%5 == 0) enemyID = "ENEMY2";
		}
		if (currentWave >= 6) {
			if (i%5 == 0) enemyID = "ENEMY2";
			if (i%6 == 0) enemyID = "ENEMY3";
		}
		if (currentWave >= 9) {
			if (i%4 == 0) enemyID = "ENEMY2";
			if (i%5 == 0) enemyID = "ENEMY3";
		}
		if (currentWave >= 12) {
			if (i%3 == 0) enemyID = "ENEMY2";
			if (i%5 == 0) enemyID = "ENEMY3";
		}

		trace(i + ". "+enemyID);
		enemyWaveList.push([enemyID, true]);

	}
	*/
	if (survivalType)
	enemyWaveList = survivalType.getEnemyWaveList(this);
}


var randID:Number = Math.round(Math.random()*1000);
function _pickBoss():void {
	trace("----------------- PICKING BOSS --- ID::"+randID);
	if (survivalType) {
		if (survivalType.doBoss) {
			//Determin is Boss Wave
			var bossWaveCount:int = survivalType.bossWaveCount;
			if (currentWave>survivalType.startBossAfterWave) {
				if (currentWave%bossWaveCount == 0) bossRound = true;
				else bossRound = false;
			} 

			//If is a Boss Wave
			if (bossRound) {
				bossWaveList = [];
				/*
				//set default
				var bossID:String = "BOSS1";

				//set Vandheer
				if (currentWave%Number(bossWaveCount*2) == 0) { 
					bossID = "BOSS2";
				}

				bossWaveList.push([bossID, true]);
				//spawn 2 bosses at once after 2 boss waves
				if (currentWave > bossWaveCount*2)
					bossWaveList.push([bossID, true]);
				//repick fewer for boss round
				_pickEnemies(true);
				*/
				bossWaveList = survivalType.getBossWaveList(this);

			}
		}
	}
}
