Actionscript.script:
import flash.display.MovieClip;

var id:Number;
var isOn:Boolean;
var gameData:MovieClip;

init ();

function init ():void {
	if (MovieClip(root)) {
		gameData = MovieClip(root);

		switch (id) {
			case 1:
				//fullscreen
				if (!gameData.fullscreen) {
					toggleOff();
				} else {
					toggleOn();
				}
			break;
			case 2:
				//particles
			if (gameData.data_particlesEnabled) {
				toggleOn();
			} else {
				toggleOff();
			}
			break;
			case 3:
				//display hud
				if (gameData.data_hideHud) {
					toggleOff();
				} else {
					toggleOn();
				}
			break;
			case 4:
				//mute
				if (gameData.isSoundMute) {
					toggleOff();
				} else {
					toggleOn();
				}
			break;
			case 5:
				//mute
				if (gameData.isMusicMute) {
					toggleOff();
				} else {
					toggleOn();
				}
			break;
		}
	}
}

function toggleOn():void {
	gotoAndStop(2);
	isOn = true;
	switch (id) {
		case 1:
			//fullscreen
			gameData.displayFullscreen();
			gameData.fullscreen = true;
		break;
		case 2:
			//particles
			gameData.data_particlesEnabled = true;
		break;
		case 3:
			//display hud
			gameData.data_hideHud = false;
		break;
		case 4:
			gameData.unMuteSound();
			break;
		case 5:
			gameData.unMuteMusic();
			break;

	}
}

function toggleOff():void {
	isOn = false;
	gotoAndStop(3);

	switch (id) {
		case 1:
			//fullscreen
			gameData.displayWindow();
			gameData.fullscreen = false;
		break;
		case 2:
			//particles
			gameData.data_particlesEnabled = false;
		break;
		case 3:
			//display hud
			gameData.data_hideHud = true;
		break;
		case 4:
			gameData.muteSound();
			break;
		case 5:
			gameData.muteMusic();
			break;
	}	
}

function toggle():void {
	if (isOn) {
		toggleOff();
	} else {
		toggleOn();
	}
}
