Actionscript.script:
import flash.display.MovieClip;

var btnArr:Array;

init();

function init():void {
	btnArr = new Array();
	btnArr = [b0, b1, b2, b3];
}

function updateVisibility(confirming:Boolean, selectedBtn:MovieClip):void {

	for (var i:int = 0; i < btnArr.length; i ++) {
		var clip:MovieClip = btnArr[i];
		if (confirming) {

			if (clip == selectedBtn) {
				if (clip.alpha < 1) clip.alpha += .1;
			} else {
				if (clip.alpha > 0) clip.alpha -= .1;
			}
		} else {
			if (clip.alpha < 1) clip.alpha += .1;
		}
	}
}
